        FROM W_RTL_SLS_IT_DY_A A 
        WHERE A.DT_WID BETWEEN '120130919000' AND '120130925000'
        GROUP BY A.PROD_WID) T1 ON D.ROW_WID=T1.PROD_WID
--活动开始前
LEFT OUTER JOIN (SELECT A.PROD_WID,SUM(A.SLS_QTY) SLS_QTY
        FROM W_RTL_SLS_IT_DY_A A 
        WHERE A.DT_WID BETWEEN '120130905000' AND '120130911000'
        GROUP BY A.PROD_WID) T2 ON D.ROW_WID=T2.PROD_WID 
--去年活动期
LEFT OUTER JOIN (SELECT A.PROD_WID,SUM(A.SLS_QTY) SLS_QTY
        FROM W_RTL_SLS_IT_DY_A A
        WHERE A.DT_WID BETWEEN '120120920000' AND '120120926000'
        GROUP BY A.PROD_WID) T3 ON D.ROW_WID=T3.PROD_WID       
        
